$(document).ready((function(){function changeColor(amount,directionIndex){for(var gradDirection=["to bottom","to top","to right","to left"],classLength=$(".sample").length,x=0;x<=classLength;x++){for(var colorTxt="",y=1;y<=amount;y++)colorTxt+=`, ${generateColor()}`;var targetClass;$(".sample"+[x]).css("background-image",`linear-gradient(${gradDirection[directionIndex]}  ${colorTxt})`)}}function generateColor(){var alpha="0123456789abcdef";alpha=alpha.split("");for(var colorCode="#",i=0;i<=5;i++){var randNum;colorCode+=alpha[Math.floor(Math.random()*alpha.length)]}return colorCode}changeColor(2,3),$(".selec-option-1").change((function(){1==$(this).val()?$(".sampleCont").addClass("squareCont"):$(".sampleCont").removeClass("squareCont")})),$(".sampleTxt").hover((function(){$(this).val("copy !")})),$("button").click((function(){changeColor(2,3)})),$("button").click((function(){var amount,direction;changeColor(parseInt($(this).prev().prev().val()),$(this).prev().val())})),$(".sampleTxt").click((function(){$(this).val($(this).parent().css("background-image"));var copyText=$(this);copyText.select(),document.execCommand("copy"),$(".overlay p").text("Copied :)"),$(".overlay").css({"background-image":`${copyText.val()}`,display:"flex","z-index":"2"}).fadeIn(1e3,(function(){$(this).fadeOut(2e3)}))}))}));